import React, { useState } from "react";
import "./LawyerCard";
const SearchBar = ({ query, setQuery, setOption }) => {

  //! =============> DEBOUNCE
  function debounce(func, timeout = 300) {
    let timer;
    return (...args) => {
      clearTimeout(timer);
      timer = setTimeout(() => { func.apply(this, args); }, timeout);
    };
  }
  function saveInput(e) {
    setQuery(e.target.value)
  }
  const processChange = debounce((e) => saveInput(e));
  //! ===================================================>


  return (
    <div className="search-bar">
      <h1>Meet our Lawyers</h1>

      <div className="select">
        <span className="search-by">Search By : </span>
        <div className="mydict">
          <div>
            <label>
              <input
                type="radio"
                name="radio"
                value="name"
                onClick={(e) => {
                  setOption("name");
                }}
              />
              <span>Name</span>
            </label>
            <label>
              <input
                type="radio"
                name="radio"
                value="profession"
                onClick={(e) => {
                  setOption("profession");
                }}
              />
              <span>Profession</span>
            </label>
            <label>
              <input
                type="radio"
                name="radio"
                value="experience"
                onClick={(e) => {
                  setOption("experience");
                }}
              />
              <span>Experience</span>
            </label>
          </div>
        </div>
      </div>
      <div className="group">
        <form action="/" method="get">
          <input
            placeholder="Search"
            type="search"
            className="input"

            onKeyUp={(e) => processChange(e)}
          />
          <svg className="searchDot" aria-hidden="true" viewBox="0 0 24 24">
            <g>
              <path d="M21.53 20.47l-3.66-3.66C19.195 15.24 20 13.214 20 11c0-4.97-4.03-9-9-9s-9 4.03-9 9 4.03 9 9 9c2.215 0 4.24-.804 5.808-2.13l3.66 3.66c.147.146.34.22.53.22s.385-.073.53-.22c.295-.293.295-.767.002-1.06zM3.5 11c0-4.135 3.365-7.5 7.5-7.5s7.5 3.365 7.5 7.5-3.365 7.5-7.5 7.5-7.5-3.365-7.5-7.5z"></path>
            </g>
          </svg>
        </form>
      </div>
    </div>
  );
};

export default SearchBar;
